        if (entry.nitrite) items.push(`NO‚ÇÇ: ${entry.nitrite} ppm`);
        if (entry.nitrate) items.push(`NO‚ÇÉ: ${entry.nitrate} ppm`);
        return items.join(' ‚Ä¢ ') || 'No measurements recorded';
