            await this.makeApiCall(`${endpoint}/${this.activeSystemId}/${id}`, {
                method: 'PUT',
                body: JSON.stringify(formData)
            });
            
            // Reload data
            await this.loadDataRecords();
            await this.updateDashboardFromData();
            
            // Refresh the edit interface
