    // Initialize keyboard shortcuts
    initializeKeyboardShortcuts() {
        document.addEventListener('keydown', (e) => {
            // Only activate shortcuts when not typing in an input
            if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA' || e.target.tagName === 'SELECT') {
                return;
            }
            
            // Check for command/ctrl key combinations
            const isModKey = e.metaKey || e.ctrlKey;
            
            if (isModKey) {
                switch(e.key.toLowerCase()) {
                    case 'p':
                        e.preventDefault();
                        this.openQuickPlantDialog();
                        break;
                    case 'h':
                        e.preventDefault();
                        this.openQuickHarvestDialog();
                        break;
                    case 'k':
                        e.preventDefault();
                        this.openCommandPalette();
                        break;
                    case '/':
                        e.preventDefault();
                        this.focusSearch();
                        break;
                }
            }
            
            // Single key shortcuts (when not in input)
            switch(e.key.toLowerCase()) {
                case '?':
                    e.preventDefault();
                    this.showKeyboardShortcuts();
                    break;
                case 'g':
                    if (!this.gKeyPressed) {
                        this.gKeyPressed = true;
                        setTimeout(() => { this.gKeyPressed = false; }, 1000);
                    } else {
                        // Double 'g' pressed
                        e.preventDefault();
                        switch(e.key.toLowerCase()) {
                            case 'p':
                                this.navigateToPlants();
                                break;
                            case 'd':
                                this.navigateToDashboard();
                                break;
                            case 'f':
                                this.navigateToFish();
                                break;
                        }
                    }
                    break;
            }
        });
    }
    
